import React, { useEffect, useState } from 'react';
import '../Styles/styles.css';

const Headers = () => {
  const [isHeaderScrolled, setIsHeaderScrolled] = useState(false);

  const headerScrolled = () => {
    if (window.scrollY > 100) {
      setIsHeaderScrolled(true);
    } else {
      setIsHeaderScrolled(false);
    }
  };

  useEffect(() => {
    headerScrolled(); // Call the function once to check on initial render

    window.addEventListener('load', headerScrolled);
    window.addEventListener('scroll', headerScrolled);

    return () => {
      window.removeEventListener('load', headerScrolled);
      window.removeEventListener('scroll', headerScrolled);
    };
  }, []);

  return (
    <>
    <i className="bi bi-list mobile-nav-toggle"></i>
    
      <header id="header" className={`fixed-top ${isHeaderScrolled ? 'header-scrolled' : ''}`}>
        <div className="container d-flex align-items-center">
          <h1 className="logo me-auto">
            <a href="index.html">Arsha</a>
          </h1>

          <a href="index.html" className="logo me-auto">
            <img src="assets/img/logo.png" alt="" className="img-fluid" />
          </a>

          <nav id="navbar" className="navbar">
            <ul>
              <li>
                <a className="nav-link scrollto active" href="#hero">
                  Home
                </a>
              </li>
              <li>
                <a className="nav-link scrollto" href="#about">
                  About
                </a>
              </li>
              <li>
                <a className="nav-link scrollto" href="#services">
                  Services
                </a>
              </li>
              <li>
                <a className="nav-link scrollto" href="#contact">
                  Contact
                </a>
              </li>
              <li>
                <a className="getstarted scrollto fs-5" href="#about">
                  Get Started
                </a>
              </li>
            </ul>
            {/* <i className="bi bi-list mobile-nav-toggle"></i> */}
          </nav>
        </div>
      </header>
    </>
  );
};

export default Headers;
